import React, { Component } from 'react';
import { observable, toJS } from 'mobx';
import { observer } from 'mobx-react';
import Header from "../components/Header";
import Navbar from "../components/Header";
import Footer from "../components/Footer";
import Router from 'next/router';

@observer
class Index extends Component {

    constructor(props) {
        super(props);
        this.state = {
            input_content : '',

            footerGap : 200, 

            fontSizeState : 50,
            fontColorState : '#000',
            fontState : null,
        };

    }



    handleChange = (paramName, event) => {
        if (paramName == 'input_content') {
            this.setState({
                input_content: event.target.value,
                errorMessage: ''
            });
        }

    };



    render() {
        const { fontSizeState, input_content } = this.state;
        return (
            <>
            <Header></Header>
                <div className=" " style={{ width: '100%', marginTop: 100, display: 'flex', alignItems: 'center', justifyContent: 'center'}}>
                    <div className="font_display_container  max-width-wrapper">

                        <div className="font_controller_area"></div>
                        <div className="font_display_area" style={{  fontSize : fontSizeState, marginLeft: 20,}}>
                            <input
                                    className="input_content"
                                    placeholder="Font Contents"
                                    name="input_content"
                                    value={this.state.input_content}
                                    style={{  fontSize : fontSizeState }}
                                    onChange={e => {
                                    this.handleChange('input_content', e);
                                    }}
                                /> 
                        </div>
                    </div>
                </div>
            <div style={{ height: 400 }}></div>
            <Footer path={'/'} footerGap={this.state.footerGap}/>

            <style 
            jsx="true"
            global="true"
            suppressHydrationWarning>
                {`
                    .input_wrapper {
                        width: 100%;
                        height: 48px;
                        border: solid 1px #eaeaea;
                        background-color: #ffffff;
                        display: flex;
                        align-items: center;
                    }
                    .input_content {
                        width : 100%;
                        height: 100%;
                        border: none;
                        outline: none;
                        margin-left: 16px;
                    }
                    .font_display_container {
                        width: calc(100% - 40px);
                        min-height: 500px;
                        display: flex;
                        align-self: center;
                        align-items: center;
                        justify-content: center;
                    }

                    .font_controller_area {
                        width: 200px;
                        min-height: 500px;
                        border: 1px solid #aaa;
                        display: flex;
                        align-items: center;
                        justify-content: center;

                    }

                    .font_display_area {
                        width: calc(100% - 250px);
                        min-height: 500px;
                        border: 1px solid #aaa;
                        display: flex;
                        align-items: center;
                        justify-content: center;

                    }
                    .font_input_area {
                        width: calc(100% - 20px);
                        height: calc(100% - 20px);
                        display: flex;
                        resize: none;
                        border : none;
                        outline : none;
                    }
                    /* Extra small devices (phones, 600px and down) */
                    @media only screen and (max-width: 600px) {
                    /* ... */
                    }
                    /* Small devices (portrait tablets and large phones, 600px and up) */
                    @media only screen and (min-width: 600px) {
                    /* ... */
                    }
                    /* Medium devices (landscape tablets, 768px and up) */
                    @media only screen and (min-width: 768px) {
                    /* ... */
                    }
                    /* Large devices (laptops/desktops, 992px and up) */
                    @media only screen and (min-width: 992px) {
                    /* ... */
                    }
                    /* Extra large devices (large laptops and desktops, 1200px and up) */
                    @media only screen and (min-width: 1200px) {
                    /* ... */
                    }
                `}
            </style>
            </>
        )
    }
}

export default Index;
