import React, { Component } from 'react';
import { observable, toJS } from 'mobx';
import { observer } from 'mobx-react';
import Header from "../components/Header";
import Navbar from "../components/Header";
import Footer from "../components/Footer";
import Router from 'next/router';

@observer
class Index extends Component {

    constructor(props) {
        super(props);
        this.state = {
            footerGap : 200, 
        };

    }
    render() {
        return (
            <>
            <Header></Header>
            <div className="controllers content-max-width-class">
                <div className="font_display_container" style={{ marginTop: 100}}>
                    <div className="font_controller_area"></div>
                    <div className="font_display_area"></div>
                </div>
            </div>
            <div style={{ height: 400 }}></div>
            <Footer path={'/'} footerGap={this.state.footerGap}/>

            <style 
            jsx="true"
            global="true"
            suppressHydrationWarning>
                {`
                    .font_display_container {
                        width: 100%;
                        min-height: 500px;
                        display: flex;
                        align-items: center;
                        justify-content: center;
                    }

                    .font_controller_area {
                        width: 200px;
                        min-height: 500px;
                        border: 1px solid #eaeaea;

                    }

                    .font_display_area {
                        width: 100%;
                        min-height: 500px;
                        border: 1px solid #eaeaea;

                    }
                `}
            </style>
            </>
        )
    }
}

export default Index;
