import React, { Component } from 'react';
import { observable, toJS } from 'mobx';
import { observer } from 'mobx-react';
import Header from "../components/Header";
import Navbar from "../components/Header";
import Footer from "../components/Footer";
import Router from 'next/router';

@observer
class Index extends Component {

    constructor(props) {
        super(props);
        this.state = {
            footerGap : 200, 
        };

    }
    render() {
        return (
            <>
            <Header></Header>
                <div className=" " style={{ width: '100%', marginTop: 100, display: 'flex', alignItems: 'center', justifyContent: 'center'}}>
                    <div className="font_display_container  max-width-wrapper">

                        <div className="font_controller_area"></div>
                        <div className="font_display_area" style={{ marginLeft: 20,}}>
                            <textarea className="font_input_area"></textarea>
                        </div>
                    </div>
                </div>
            <div style={{ height: 400 }}></div>
            <Footer path={'/'} footerGap={this.state.footerGap}/>

            <style 
            jsx="true"
            global="true"
            suppressHydrationWarning>
                {`
                    .font_display_container {
                        width: calc(100% - 40px);
                        min-height: 500px;
                        display: flex;
                        align-self: center;
                        align-items: center;
                        justify-content: center;
                    }

                    .font_controller_area {
                        width: 200px;
                        min-height: 500px;
                        border: 1px solid #aaa;
                        display: flex;
                        align-items: center;
                        justify-content: center;

                    }

                    .font_display_area {
                        width: calc(100% - 250px);
                        min-height: 500px;
                        border: 1px solid #aaa;
                        display: flex;
                        align-items: center;
                        justify-content: center;

                    }
                    .font_input_area {
                        width: calc(100% - 20px);
                        height: calc(100% - 20px);
                        resize: none;
                        border : none;

                    }
                `}
            </style>
            </>
        )
    }
}

export default Index;
