import React, { Component } from 'react';
import { observable, toJS } from 'mobx';
import { observer } from 'mobx-react';
import Router from 'next/router';
import Slider from 'react-rangeslider'
import Header from "../components/Header";
import Navbar from "../components/Header";
import Footer from "../components/Footer";
import VerticalSlider from "../components/VerticalSlider";
import HorizontalSlider from "../components/HorizontalSlider";

import { ChromePicker } from 'react-color'; 



@observer
class Index extends Component {

    constructor(props) {
        super(props);
        this.state = {
            input_content : '',

            footerGap : 200, 

            fontSizeState : 50,
            fontColorState : '#000',
            fontState : null,
        };

    }



    handleChange = (paramName, event) => {
        if (paramName == 'input_content') {
            this.setState({
                input_content: event.target.value,
                errorMessage: ''
            });
        }

    };

    handleChangeComplete = (color) => {
        this.setState({ fontColorState: color.hex });
      };


      handleFontSizeChange = value => {
        this.setState({
            fontSizeState: value
        })
      };

    render() {
        const { fontSizeState, input_content } = this.state;
        return (
            <>
            <Header></Header>
                <div className=" " style={{ width: '100%', marginTop: 200, display: 'flex', alignItems: 'center', justifyContent: 'center', flexDirection: 'column'}}>
                    <div className="font_display_container  max-width-wrapper">

                        <div className="font_display_area" style={{  fontSize : 20,display: 'flex', alignItems: 'flex-start', justifyContent: 'center' }}>
                                <div className="left " >
                                    <div className="font_display_control_label" 
                                    style={{  fontFamily: "Noto Sans KR"}}>
                                        사이즈 - Size

                                        <Slider
                                        min={0}
                                        max={100}
                                        value={fontSizeState}
                                        style={{ color: '#000'}}
                                        onChangeStart={this.handleChangeStart}
                                        onChange={this.handleFontSizeChange}
                                        />
                                        <div className='value'>{fontSizeState}</div>
                                    </div>
                                    <div className="font_display_control_label" 
                                    style={{  fontFamily: "Noto Sans KR"}}>
                                        애니메이션 - Animation
                                    </div>
                                </div>
                                <div className="right " >

                                    <div className="font_display_control_label" 
                                    style={{  fontFamily: "Noto Sans KR"}}>
                                        색상 - Color
                                        <br/>
                                    </div>
                                        <ChromePicker
                                            color={ this.state.fontColorState }
                                            onChangeComplete={ this.handleChangeComplete }

                                        />

                                </div>

                            <div className="font_display_controller"></div>
                        </div>
                    </div>

                    <div className="font_display_container  max-width-wrapper"  style={{  marginTop: 40, }}>

                        <div className="font_display_area" style={{  fontSize : fontSizeState,}}>
                            <textarea
                                    className="input_content"
                                    placeholder="Font Contents"
                                    name="input_content"
                                    value={this.state.input_content}
                                    style={{  fontSize : fontSizeState }}
                                    onChange={e => {
                                    this.handleChange('input_content', e);
                                    }}
                                /> 
                        </div>
                    </div>
                </div>
            <div style={{ height: 400 }}></div>
            <Footer path={'/'} footerGap={this.state.footerGap}/>

            <style 
            jsx="true"
            global="true"
            suppressHydrationWarning>
                {`
                    .rangeslider__fill {
                        background-color: #000;
                    }
                    div.left {
                    width: 50%;
                    float: left;
                    box-sizing: border-box;
                    }
                    div.right {
                    width: 50%;
                    float: right;
                    box-sizing: border-box;
                    }
                    .font_display_control_label {
                        height: 50px;
                        color : #0a0a0a;
                        width: 100%;
                        align-items: center;
                        justify-content: flex-start;
                    }
                    .input_wrapper {
                        width: 100%;
                        height: 100%;
                        border: solid 1px #eaeaea;
                        background-color: #ffffff;
                        display: flex;
                        align-items: center;
                    }
                    .input_content {
                        width : 100%;
                        height: 100%;
                        min-height: 450px;
                        margin-left: 16px;
                        resize: none;
                        border : none;
                        outline : none;
                    }
                    .font_display_container {
                        width: calc(100% - 40px);
                        min-height: 500px;
                        display: flex;
                        align-self: center;
                        align-items: center;
                        justify-content: center;
                    }

                    .font_controller_area {
                        width: 300px;
                        min-height: 500px;
                        border: 1px solid #aaa;
                        display: flex;
                        align-items: center;
                        justify-content: center;

                    }

                    .font_display_area {
                        width: calc(100% - 320px);
                        min-height: 500px;
                        border: 1px solid #aaa;
                        display: flex;
                        align-items: center;
                        justify-content: center;
                        padding : 20px;

                    }
                    .font_input_area {
                        width: calc(100% - 20px);
                        height: calc(100% - 20px);
                        display: flex;
                    }
                    /* Extra small devices (phones, 600px and down) */
                    @media only screen and (max-width: 600px) {
                    /* ... */
                        .font_display_area {
                            flex-direction: column;
                        }
                    }
                    /* Small devices (portrait tablets and large phones, 600px and up) */
                    @media only screen and (min-width: 600px) {
                    /* ... */
                    .font_display_area {
                        flex-direction: column;
                    }
                    }
                    /* Medium devices (landscape tablets, 768px and up) */
                    @media only screen and (min-width: 768px) {
                    /* ... */
                    .font_display_area {
                        flex-direction: column;
                    }
                    }
                    /* Large devices (laptops/desktops, 992px and up) */
                    @media only screen and (min-width: 992px) {
                    /* ... */
                    .font_display_area {
                        flex-direction: row;
                    }
                    }
                    /* Extra large devices (large laptops and desktops, 1200px and up) */
                    @media only screen and (min-width: 1200px) {
                    /* ... */
                    .font_display_area {
                        flex-direction: row;
                    }
                    }
                `}
            </style>
            </>
        )
    }
}

export default Index;
